import plotly.graph_objects as go

# Data points based on US Census Bureau historical series
years = [2009, 2010, 2011, 2012, 2013, 2014, 2015, 2016, 2017, 2018, 2019, 2020, 2021, 2022, 2023, 2024]
opm = [14.3, 15.1, 15.0, 15.0, 14.5, 14.8, 13.5, 12.7, 12.3, 11.8, 10.5, 11.4, 11.6, 11.5, 11.1, 10.6]
spm = [15.1, 15.3, 16.1, 16.0, 15.5, 15.3, 14.3, 13.9, 13.9, 12.8, 11.8, 9.1, 7.8, 12.4, 12.9, 12.9]

fig = go.Figure()

# Official Poverty Rate
fig.add_trace(go.Scatter(x=years, y=opm, name='Official Measure (OPM)',
                         line=dict(color='#1B263B', width=2, dash='dot')))

# Supplemental Poverty Measure
fig.add_trace(go.Scatter(x=years, y=spm, name='Supplemental Measure (SPM)',
                         line=dict(color='#e74c3c', width=4)))

fig.update_layout(
    title='U.S. Poverty Trends: Official vs. Supplemental Realities',
    xaxis_title='Year',
    yaxis_title='Percentage of Population (%)',
    plot_bgcolor='white',
    hovermode='x unified',
    legend=dict(orientation="h", yanchor="bottom", y=1.02, xanchor="right", x=1)
)

fig.update_xaxes(showgrid=True, gridcolor='#F0F0F0')
fig.update_yaxes(showgrid=True, gridcolor='#F0F0F0', ticksuffix='%')

fig.show()
```

